import mongoose from 'mongoose';
import bcrypt from 'bcrypt';

const studentSchema = mongoose.Schema({
    name : {type:String},
    email : {
        type:String,
        lowercase : true
    },
    password : {type: String},
    authToken : {
        type:String,
        default :  ""
    },
    activeClasses : [{
        type:mongoose.Types.ObjectId,
        ref : 'Subject'
    }],
    PresentAssignments : [{
        type:mongoose.Types.ObjectId,
        ref:'AssignmentPost'
    }],
    CompletedAssignments : [{
        type : mongoose.Types.ObjectId,
        ref:'AssignmentPost'
    }],
    completedTests : [{
        type:mongoose.Types.ObjectId,
        ref : 'AssignmentPost'
